&sql(open UntList) &sql(fetch UntList)
while (SQLCODE = 0) { set $list(unitIdList,*+1) = tempUnitId &sql(fetch UntList) }
&sql(close UntList)
set length = $listlength(phoneIdList) set untLength = $listlength(objectIdList)
