set unitId = $list(unitIdList, i) &sql(SELECT PhoneName INTO :unitName FROM iknowAL_Phones_Stat.ReviewStat WHERE PhoneId = :unitId)
// Obtain the final evaluation of phone &sql(SELECT AVG(NULLIF(iKnowRating, 0)), AVG(NULLIF(iKnowEmarkersRating,0)), AVG(NULLIF(iKnowRatingComfort,0)), AVG(NULLIF(iKnowRatingPerfomance, 0)), AVG(NULLIF(iKnowRatingLink, 0)), AVG(NULLIF(iKnowRatingCamera,0)), AVG(NULLIF(AuthorRating,0)) INTO :iKnowRating, :iKnowEmarkersRating, :iKnowRatingComfort, :iKnowRatingPerfomance, :iKnowRatingLink, :iKnowRatingCamera, :authorRating FROM iknowAL_Phones_Stat.Review WHERE PhoneId = :phoneId)
// Obtain the final evaluation of phone &sql(SELECT DISTINCT DomainID INTO :domID FROM iknowAL_Dictionary.MarksReview)
&sql(declare curCtg1 cursor for select id, ctgName into :ccID, :ccName from iknowal_dictionary.category) &sql(open curCtg1) &sql(fetch curCtg1)
while (SQLCODE = 0) { &sql(SELECT AVG(NULLIF(Mark, 0)) INTO :avgUnt FROM iknowAL_Dictionary.MarksReview WHERE UID = :objectId & CtgID = :ccID)
&sql(INSERT OR UPDATE INTO iknowAL_Dictionary.MarksUnit (DomainID, UID, UName, CtgID, Mark) VALUES (:domID, :unitId, :unitName, :ccID, :avgUnt)) &sql(fetch curCtg1) }
&sql(close curCtg1)
if ($data(^preferences("NEUTRALHIGHBORDER"))) { set neutralHighBorder = ^preferences("NEUTRALHIGHBORDER") } else { write "Settings data corruption.", ! return ERROR("Settings data corruption. Reload the domain or fix the settings data.") }
if ($data(^preferences("NEUTRALLOWBORDER"))) { set neutralLowBorder = ^preferences("NEUTRALLOWBORDER") } else { write "Settings data corruption.", ! return ERROR("Settings data corruption. Reload the domain or fix the settings data.") }
&sql(SELECT COUNT(ID) INTO :positiveReviews FROM iknowAL_Phones_Stat.Review WHERE PhoneId = :phoneId AND iKnowRating > :neutralHighBorder) &sql(SELECT COUNT(ID) INTO :negativeReviews FROM iknowAL_Phones_Stat.Review WHERE PhoneId = :phoneId AND iKnowRating < :neutralLowBorder) &sql(SELECT COUNT(ID) INTO :neutralReviews FROM iknowAL_Phones_Stat.Review WHERE PhoneId = :phoneId AND iKnowRating <= :neutralHighBorder AND iKnowRating >= :neutralLowBorder) &sql(SELECT COUNT(ID) INTO :numberOfReviews FROM iknowAL_Phones_Stat.Review WHERE PhoneId = :phoneId AND iKnowRating > 0)
set positiveReviewsPCT = 0 set negativeReviewsPCT = 0 set neutralReviewsPCT = 0
if (numberOfReviews > 0) { set positiveReviewsPCT = positiveReviews / numberOfReviews * 100 set negativeReviewsPCT = negativeReviews / numberOfReviews * 100 set neutralReviewsPCT = neutralReviews / numberOfReviews * 100 }
// Write the results in the table &sql(INSERT OR UPDATE INTO iknowAL_Phones_Stat.Phone (PhoneId, PhoneName, NegativeReviewsPCT, PositiveReviewsPCT, NeutralReviewsPCT, ReviewsCount, UserRating, iKnowRating, iKnowEmarkersRating, iKnowRatingComfort, iKnowRatingPerfomance, iKnowRatingLink, iKnowRatingCamera) VALUES (:phoneId, :phoneName, :negativeReviewsPCT, :positiveReviewsPCT, :neutralReviewsPCT, :numberOfReviews, :authorRating, :iKnowRating, :iKnowEmarkersRating, :iKnowRatingComfort, :iKnowRatingPerfomance, :iKnowRatingLink, :iKnowRatingCamera)) }
return $$$OK


