else {
	write "Settings data corruption.", !
	return ERROR("Settings data corruption. GRADESUSPECT Parameter is missing. Reload the domain or fix the settings data.")	
}

&sql(SELECT %DLIST(PositiveEmotionalSaturation) INTO :pesList FROM iknowAL_Phones_Stat.ReviewStat WHERE PhoneId = :phoneId)
&sql(SELECT AVG(iKnowRating) INTO :meanRating FROM iknowAL_Phones_Stat.Review WHERE PhoneId = :phoneId)

set stdDev = ##class(iknowAL.Phones.Stat.Statistics).StdDev(pesList)
set mean = ##class(iknowAL.Phones.Stat.Statistics).Mean(pesList)

&sql(SELECT %DLIST(ID) INTO :idList FROM iknowAL_Phones_Stat.ReviewStat WHERE PhoneId = :phoneId)

if ($data(^preferences("PESSUSPECT"))) {
		set pesSuspect = ^preferences("PESSUSPECT")
}
else {
	write "Settings data corruption.", !
	return ERROR("Settings data corruption. Reload the domain or fix the settings data.")	
}

set length = $listlength(idList)

for i=1:1:length {

	 set suspect = 0

     set reviewStat = ##class(iknowAL.Phones.Stat.ReviewStat).%OpenId($list(idList, i))	

     if (reviewStat.EmotionalSaturation = 0) { continue }		 
	 if ('($data(stdDev)) || (stdDev = 0)) { continue }

     if (($zabs(reviewStat.PositiveEmotionalSaturation - mean)) > (3 * stdDev)) {
	     set suspect = suspect + 1
     }


     if ((reviewStat.PositiveEmotionalSaturation / reviewStat.EmotionalSaturation) > pesSuspect) {
	     set suspect = suspect + 1
     }

     if (reviewStat.iKnowRating - mean > gradeSuspectDiff) {
     	set suspect = suspect + 1
     }

     set reviewId = reviewStat.ReviewId 

	 &sql(UPDATE iknowAL_Phones_Stat.Review SET Suspect = :suspect WHERE SourceId = :reviewId)

	 kill reviewStat	 
}

return $$$OK

	set usefulness = 0
	set us1 = 0
	set us2 = 0
	set us3 = 0

	&sql(SELECT %DLIST(EmotionalSaturation), %DLIST(ID) INTO :esList, :idList FROM iknowAL_Phones_Stat.ReviewStat WHERE PhoneId = :phoneId)	

	if ($listlength(esList) > 0) {
		set meanES = ##class(iknowAL.Phones.Stat.Statistics).Mean(esList)
		set stdevES = ##class(iknowAL.Phones.Stat.Statistics).StdDev(esList)
	}
	else {
		return $$$OK
	}

	set length = $listlength(idList)

	for i=1:1:length {

	     set reviewStat = ##class(iknowAL.Phones.Stat.ReviewStat).%OpenId($list(idList, i))	

	     if (reviewStat.EmotionalSaturation = 0) { continue }

		 if (('$data(stdevES) || (stdevES = 0))) { 
		 	set us1 = 0 
		 }
		 else {
		 	set us1 = 1 - (($zabs(reviewStat.EmotionalSaturation - meanES)) / (3 * stdevES)) 
		 }

		 if (us1 < 0) { 
		 	set us1 = 0 
		 }

	     set reviewId = reviewStat.ReviewId 

 		 &sql(SELECT iKnowRatingPerfomance, iKnowRatingComfort, iKnowRatingLink, iKnowRatingCamera
 		 	  INTO :perfomance, :comfort, :link, :camera 
 		 	  FROM iknowAL_Phones_Stat.Review WHERE SourceId = :reviewId)

		 set us2 = $case(perfomance, "":0, :1) + $case(comfort, "":0, :1) + $case(link, "":0, :1) + $case(camera, "":0, :1)

		 set us2 = us2 / 4

		 if (reviewStat.PositiveEmotionalSaturation >= reviewStat.NegativeEmotionalSaturation) {
			 set us3 = 2 * (reviewStat.NegativeEmotionalSaturation / reviewStat.EmotionalSaturation)
		 }
		 else {
			 set us3 = 2 * (reviewStat.PositiveEmotionalSaturation / reviewStat.EmotionalSaturation)
		 }

		 kill reviewStat

		 set usefulness = (us1 + us2 + us3) / 3 * 100

		 &sql(UPDATE iknowAL_Phones_Stat.Review SET Usefulness = :usefulness WHERE SourceId = :reviewId)

	}

	return $$$OK

set tSC = $$$OK

set tST = ##class(iknowAL.Phones.Stat.ReviewStat).%KillExtent()

if (tST '= $$$OK ) {
	set tSC = $$$ERROR("Can't KillExtent for iknowAL.Phones.Stat.ReviewStat")	
}

set tST = ##class(iknowAL.Phones.Stat.PhoneStat).%KillExtent()

if (tST '= $$$OK ) {
	set tSC = $$$ERROR("Can't KillExtent for iknowAL.Phones.Stat.PhoneStat")	
}

return tSC

set domainId = ##class(iknowAL.util.main).GetDomainId(domName)
set markerCategory = ""

// Set the filter with PhoneId
